<?phpnamespace app\home\controller;use think\Controller;use think\Db;use think\response\Json;use think\Session;use think\Cookie;class Base extends Controller {    public $session_id;    public $cateTrre = array();    /*     * 初始化操作     */    public function _initialize() {        Session::start();        header("Cache-control: private");  // history.back返回后输入框值丢失问题    	$this->session_id = session_id(); // 当前的 session_id        define('SESSION_ID',$this->session_id); //将当前的session_id保存为常量，供其它方法调用        // 判断当前用户是否手机        if(isMobile())            cookie('is_mobile','1',3600);        else            cookie('is_mobile','0',3600);        $this->public_assign();        $this->category();        $this->friendlink();    }    public function category(){        $category = M('post_category')->where('is_open',1)->select();        $this->assign('category',$category);// 友情链接    }    public function friendlink(){        $friendlink = M('friend_link')->where('is_show',1)->select();        $this->assign('friendlink',$friendlink);// 友情链接    }    /**     * 保存公告变量到 smarty中 比如 导航     */    public function public_assign()    {       $tpshop_config = array();       $tp_config = M('config')->cache(true,TPSHOP_CACHE_TIME)->select();       foreach($tp_config as $k => $v){       	  if($v['name'] == 'hot_keywords'){       	  	 $tpshop_config['hot_keywords'] = explode('|', $v['value']);       	  }          $tpshop_config[$v['inc_type'].'_'.$v['name']] = $v['value'];       }       $this->assign('tpshop_config', $tpshop_config);    }    public function ajaxReturn($data) {        exit(json_encode($data));    }}